mapper.insert:
/* cash.insertWebCashInfo */
	INSERT	cm_console.CM_WEB_CASH_HIST (
			CORP_ID
	,		CASH_ID
	,		PAYMENT_ID
	,		PAY_MTD
	,		STATUS
	,		CASH_CHARG_AMOUNT
	,		REG_DT
	) VALUES (
			#{corpId}
	,		#{cashId}
	,		#{orderId}
	,		#{payMtd}
	,		#{status}
	,		#{amount}
	,		NOW()
	)

mapper.select:
/* cash.selectWebCashInfo */
	SELECT	PAYMENT_ID			AS ORDER_ID
	,		CASH_CHARG_AMOUNT	AS AMOUNT
	FROM	cm_console.CM_WEB_CASH_HIST
	WHERE	PAYMENT_ID = #{orderId}
