mapper.insert:
/* common.insertImageFile - 고객사별 이미지 관리 등록 */
    INSERT CM_IMAGE_FILE
    (
        CORP_ID
        , USE_CH_INFO
        , ORIGIN_FILE_NAME
        , IMAGE_FILE_NAME
        , IMAGE_FILE_PATH
        , REG_DT
        , UPD_DT
        , REG_ID
        , UPD_ID
    )
    VALUES
    (
        #{corpId}
        , #{useChInfo}
        , #{originFileName}
        , #{imageFileName}
        , #{imageFilePath}
        , NOW()
        , NOW()
        , #{loginId}
        , #{loginId}
    )

mapper.sql:
AND CORP_ID = #{corpId}
AND JSON_EXTRACT(USE_CH_INFO, CONCAT('$.', #{useChInfo})) = 'Y'

    AND IMAGE_FILE_SEQ IN
    #{item}

mapper.select:
/* common.selectImageList - 고객사별 이미지 리스트 조회 */ SELECT IMAGE_FILE_SEQ , CORP_ID , USE_CH_INFO , ORIGIN_FILE_NAME , IMAGE_FILE_NAME , IMAGE_FILE_PATH , REG_DT , UPD_DT , REG_ID , UPD_ID FROM CM_IMAGE_FILE WHERE 1=1
ORDER BY REG_DT DESC
LIMIT ${offset}, #{listSize}

mapper.delete:
/* template.deletePushTemplate - 이미지 삭제 */
    DELETE
    FROM CM_IMAGE_FILE
    WHERE IMAGE_FILE_SEQ IN
    #{item}
        AND CORP_ID = #{corpId}
        AND JSON_EXTRACT(USE_CH_INFO, CONCAT('$.', #{useChInfo})) = 'Y'
