mapper.select:
/* integratedSend.selectIntegratedSendList - 통합발송 템플릿 리스트 조회 */ SELECT @rownum:=@rownum+1 AS ROW_NUM , A.PROJECT_ID , CASE WHEN A.PROJECT_ID = 'ALL' THEN '프로젝트 공용' ELSE '프로젝트 개별' END AS otherProjectUseYn , A.TMPLT_CODE , A.MSG_KIND , cm_console.fnc_get_cd_name('MSG_KIND', A.MSG_KIND, 1) AS MSG_KIND_NAME , A.MSG_TYPE , cm_console.fnc_get_cd_name('MSG_TYPE', A.MSG_TYPE, 1) AS MSG_TYPE_NAME , A.TMPLT_TITLE , A.TMPLT_INFO , A.TMPLT_TYPE , A.USE_YN
, REPLACE(A.TMPLT_INFO ->> '$[0].chTypeList', 'SMSMMS', 'SMS/MMS') AS TMPLT_CHANNEL , JSON_EXTRACT(A.TMPLT_INFO, '$[*].chType') CHECKED_CHANNEL , A.REG_ID , cm_console.fnc_get_user_name(A.REG_ID) AS REG_NM , A.REG_DT FROM (SELECT @rownum:=0) TMP, cm.CM_SMART_TMPLT A WHERE 1=1
ORDER BY A.REG_DT DESC
LIMIT #{offset}, #{listSize}
