mapper.select:
/* 통합발송 템플릿 리스트 조회 */ SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM, B.* FROM ( SELECT A.PROJECT_ID, CASE WHEN A.PROJECT_ID = 'ALL' THEN 'Y' ELSE 'N' END AS OTHER_PROJECT_USE_YN, CASE WHEN A.PROJECT_ID = 'ALL' THEN '공용' ELSE '전용' END AS OTHER_PROJECT_USE_YN_NAME, A.TMPLT_CODE, A.MSG_KIND, C.CODE_NAME_1 AS MSG_KIND_NAME, A.MSG_TYPE, D.CODE_NAME_1 AS MSG_TYPE_NAME, A.TMPLT_TITLE, A.TMPLT_INFO, A.TMPLT_TYPE, E.CODE_NAME_1 AS TMPLT_STATUS_NAME, A.TMPLT_INFO ->> '$[0].chTypeList' AS TMPLT_CHANNEL, JSON_EXTRACT(A.TMPLT_INFO, '$[*].chType') CHECKED_CHANNEL, A.REG_ID, cm_console.fnc_get_user_name(A.REG_ID) AS REG_NM, DATE_FORMAT(A.REG_DT, '%Y-%m-%d %H:%i:%S') AS REG_DT FROM cm.CM_SMART_TMPLT A LEFT OUTER JOIN cm_bo.CM_CODE C ON A.MSG_KIND = C.CODE_VAL_1 AND C.CODE_TYPE_CD = 'MSG_KIND' LEFT OUTER JOIN cm_bo.CM_CODE D ON A.MSG_TYPE = D.CODE_VAL_1 AND D.CODE_TYPE_CD = 'MSG_TYPE' LEFT OUTER JOIN cm_bo.CM_CODE E ON A.TMPLT_STATUS = E.CODE_VAL_1 AND E.CODE_TYPE_CD = 'MULTISEND_TMPLT_STATUS' , (SELECT @ROWNUM:=0) TMP WHERE 1=1
ORDER BY A.REG_DT DESC ) B
LIMIT #{offset}, #{listSize}

mapper.insert:
		/* integraedtemplate.insertIntegratedTemplate - 통합 템플릿 등록 */
		INSERT
			INTO cm.CM_SMART_TMPLT
		(
			PROJECT_ID
            , TMPLT_CODE
            , MSG_KIND
            , MSG_TYPE
            , TMPLT_TITLE
			, TMPLT_INFO
			, TMPLT_TYPE
			, TMPLT_STATUS
			, USE_YN
            , REG_DT
            , REG_ID
            , UPD_DT
            , UPD_ID
            , CORP_ID
        ) VALUES (
            #{projectId}
            , #{multiSendTmpltCode}
            , #{msgKind}
            , #{msgType}
            , #{tmpltTitle}
            , #{tmpltInfo}
            , #{tmpltType}
			, #{tmpltStatus}
			, #{useYn}
            , NOW()
            , #{userId}
            , NOW()
            , #{userId}
            , #{corpId}
        )
        ON DUPLICATE KEY UPDATE MSG_KIND = #{msgKind}
					            , MSG_TYPE = #{msgType}
					            , TMPLT_TITLE = #{tmpltTitle}
					            , TMPLT_INFO = #{tmpltInfo}
					            , TMPLT_TYPE = #{tmpltType}
					            , TMPLT_STATUS = #{tmpltStatus}
					            , USE_YN = #{useYn}
					            , UPD_DT = NOW()
					            , UPD_ID = #{userId}
					            , CORP_ID = #{corpId}

mapper.delete:
/* integraedtemplate.deleteIntegratedTemplate - 통합 템플릿 삭제 */
DELETE
FROM cm.CM_SMART_TMPLT
WHERE 1=1
AND TMPLT_TYPE = 'M'
AND TMPLT_CODE IN #{item}
