mapper.select:
/* channel.selectRcsBrandListCnt */
SELECT	COUNT(1) AS CNT
FROM	(
	
) AA
ORDER BY AA.REG_DT DESC

/* channel.selectRcsBrandList */
SELECT	*
FROM	(
	
) AA
ORDER BY AA.REG_DT DESC

mapper.insert:
/* channel.insertRcsReqTemp */
INSERT cm.CM_RCS_BRAND_REQ (
	YM
,	BRAND_ID
,	BRAND_REQ_KEY
,	CORP_ID
,	PROJECT_ID
,	INCOMING_TYPE
,	PROC_TYPE
,	BRAND_NAME
,	BRAND_DESC
,	CATEGORY_ID
,	SUB_CATEGORY_ID
,	BRAND_INFO
,	REG_DT
,	BRAND_KEY
,	APPROVAL_STATUS
) VALUES
(
	LAST_DAY(NOW() - interval 1 month) + interval 1 DAY 
,	#{brandId}
,	'TEMP'
,	#{corpId}
,	#{projectId}
,	'Y'
,	'Y'
,	#{name}
,	#{description}
,	#{categoryId}
,	#{subCategoryId}
,	#{brandInfo}
,	NOW()
,	'BRAND_KEY'
,	'TEMP'
)

mapper.update:
/* channel.updateRcsReqTemp */
UPDATE	cm.CM_RCS_BRAND_REQ 
SET		BRAND_NAME			= #{name}
,		BRAND_DESC			= #{description}
,		CATEGORY_ID			= #{categoryId}
,		SUB_CATEGORY_ID		= #{subCategoryId}
,		BRAND_INFO			= #{brandInfo}
WHERE	BRAND_ID			= #{brandId}

mapper.delete:
/* channel.deleteRcsReqTemp */
DELETE FROM cm.CM_RCS_BRAND_REQ
WHERE	BRAND_ID = #{brandId}
AND		BRAND_REQ_KEY = 'TEMP'

/* channel.deleteRcsChatbotReqTemp */
DELETE FROM cm.CM_RCS_CHATBOT_REQ
WHERE	BRAND_ID = #{brandId}
AND		CHATBOT_REQ_KEY = 'TEMP'

mapper.insert:
/* channel.insertRcsChatbotReqTemp */ INSERT cm.CM_RCS_CHATBOT_REQ ( YM , CHATBOT_ID , CHATBOT_REQ_KEY , CORP_ID , BRAND_ID , INCOMING_TYPE , PROC_TYPE , SVC_TYPE , CHATBOT_INFO
, REG_DT ) VALUES ( LAST_DAY(NOW() - interval 1 month) + interval 1 DAY , #{chatbotId} , 'TEMP' , #{corpId} , #{brandId} , 'C' , 'C' , 'a2p' , #{chatbotInfo}
, now() )
