mapper.select:
/* channel.selectRcsBrandListCnt */
SELECT	COUNT(1) AS CNT
FROM	(
	
) AA
ORDER BY AA.REG_DT DESC

/* channel.selectRcsBrandList */
SELECT	*
FROM	(
	
) AA
ORDER BY AA.REG_DT DESC

mapper.sql:
SELECT A.* FROM ( SELECT BRAND.* , case @vData when BRAND_ID then @vRank := @vRank + 1 else @vRank := 1 end AS RANKING , (@vData:=BRAND_ID) AS vData FROM ( select 'BRAND' AS TB_NAME , A.BRAND_ID , A.PROJECT_ID , A.CORP_ID , A.BRAND_NAME , '' as BRAND_DESC , A.BRAND_INFO , CASE WHEN A.PROJECT_ID = 'ALL' THEN 'Y' ELSE 'N' END AS OTHER_PROJECT_USE_YN , ( SELECT COUNT(MESSAGEBASE_ID) FROM cm_console.CM_RCS_TMP_MSGBASE WHERE BRAND_ID = A.BRAND_ID ) AS TMPL_CNT , ( SELECT COUNT(CHATBOT_ID) FROM cm.CM_RCS_CHATBOT WHERE BRAND_ID = A.BRAND_ID ) AS CALLBACK_CNT , CASE WHEN A.APPROVAL_DT != '' AND APPROVAL_DT IS NOT NULL THEN '승인완료' ELSE '' END AS APPROVAL_STATUS , case when A.UPD_DT is null or A.UPD_DT='' then A.REG_DT else A.UPD_DT end AS REG_DT , '' AS MAIN_MDN , '' AS MAIN_TITLE from cm.CM_RCS_BRAND A WHERE A.CORP_ID = #{corpId} and (A.PROJECT_ID = #{projectId} OR A.PROJECT_ID = 'ALL' ) UNION ALL select 'BRAND_REQ' AS TB_NAME , A.BRAND_ID , A.PROJECT_ID , A.CORP_ID , A.BRAND_NAME , A.BRAND_DESC , A.BRAND_INFO , CASE WHEN A.PROJECT_ID = 'ALL' THEN 'Y' ELSE 'N' END AS OTHER_PROJECT_USE_YN , ( SELECT COUNT(MESSAGEBASE_ID) FROM cm_console.CM_RCS_TMP_MSGBASE WHERE BRAND_ID = A.BRAND_ID ) AS TMPL_CNT , ( SELECT COUNT(CHATBOT_ID) FROM cm.CM_RCS_CHATBOT WHERE BRAND_ID = A.BRAND_ID ) AS CALLBACK_CNT , '' AS APPROVAL_STATUS
, A.REG_DT , B.MAIN_MDN , B.MAIN_TITLE from cm.CM_RCS_BRAND_REQ A left outer join ( SELECT crcr.BRAND_ID , REPLACE(JSON_EXTRACT(crcr.CHATBOT_INFO , '$.mdn[0]'), "\"", "") as MAIN_MDN , REPLACE(JSON_EXTRACT(crcr.CHATBOT_INFO , '$.subTitle[0]'), "\"", "") as MAIN_TITLE FROM cm.CM_RCS_CHATBOT_REQ crcr where JSON_EXTRACT(crcr.CHATBOT_INFO , '$.isMainNum[0]') = true ) B ON A.BRAND_ID = B.BRAND_ID WHERE A.CORP_ID = #{corpId} and (A.PROJECT_ID = #{projectId} OR A.PROJECT_ID = 'ALL' ) ) BRAND, (SELECT @vRank:=0, @vData:='') as r ORDER BY BRAND.BRAND_ID, BRAND.REG_DT ) A WHERE RANKING = 1
AND A.BRAND_ID = #{srcBrandText}
AND A.BRAND_NAME = #{srcBrandText}

mapper.insert:
/* channel.insertRcsReqTemp */
INSERT cm.CM_RCS_BRAND_REQ (
	YM
,	BRAND_ID
,	BRAND_REQ_KEY
,	CORP_ID
,	PROJECT_ID
,	INCOMING_TYPE
,	PROC_TYPE
,	BRAND_NAME
,	BRAND_DESC
,	CATEGORY_ID
,	SUB_CATEGORY_ID
,	BRAND_INFO
,	REG_DT
,	BRAND_KEY
,	APPROVAL_STATUS
) VALUES
(
	LAST_DAY(NOW() - interval 1 month) + interval 1 DAY 
,	#{brandId}
,	'TEMP'
,	#{corpId}
,	#{projectId}
,	'Y'
,	'Y'
,	#{name}
,	#{description}
,	#{categoryId}
,	#{subCategoryId}
,	#{brandInfo}
,	NOW()
,	'BRAND_KEY'
,	'TEMP'
)

mapper.update:
/* channel.updateRcsReqTemp */
UPDATE	cm.CM_RCS_BRAND_REQ 
SET		BRAND_NAME			= #{name}
,		BRAND_DESC			= #{description}
,		CATEGORY_ID			= #{categoryId}
,		SUB_CATEGORY_ID		= #{subCategoryId}
,		BRAND_INFO			= #{brandInfo}
WHERE	BRAND_ID			= #{brandId}

mapper.delete:
/* channel.deleteRcsReqTemp */
DELETE FROM cm.CM_RCS_BRAND_REQ
WHERE	BRAND_ID = #{brandId}
AND		BRAND_REQ_KEY = 'TEMP'

/* channel.deleteRcsChatbotReqTemp */
DELETE FROM cm.CM_RCS_CHATBOT_REQ
WHERE	BRAND_ID = #{brandId}
AND		CHATBOT_REQ_KEY = 'TEMP'

mapper.insert:
/* channel.insertRcsChatbotReqTemp */ INSERT cm.CM_RCS_CHATBOT_REQ ( YM , CHATBOT_ID , CHATBOT_REQ_KEY , CORP_ID , BRAND_ID , INCOMING_TYPE , PROC_TYPE , SVC_TYPE , CHATBOT_INFO
, REG_DT ) VALUES ( LAST_DAY(NOW() - interval 1 month) + interval 1 DAY , #{chatbotId} , 'TEMP' , #{corpId} , #{brandId} , 'C' , 'C' , 'a2p' , #{chatbotInfo}
, now() )

mapper.select:
/* channel.selectPushManageList */
SELECT	COUNT(1) AS CNT
FROM	cm_push.T_PUSH_CONFIG A 
WHERE	1 = 1
AND		( A.PROJECT_ID = #{saveProjectId} OR A.PROJECT_ID = 'ALL' )
AND		A.CORP_ID	= #{corpId}

mapper.insert:
		/* channel.insertPushManage */
		INSERT cm_push.T_PUSH_CONFIG
		(
			APP_ID
		,	APP_NM
		,	CORP_ID
		,	PROJECT_ID
		,	SERVICE_STATUS
		,	FCM_PACKAGE_NAME
		,	FCM_SERVER_KEY
		,	SENDER_ID
		,	APNS_CETIFICATION
		,	APNS_FILE_NAME
		,	IOS_APP_ID
		,	TEAM_KEY
		,	KEY_ID
		,	REG_ID
		,	REG_DT
		)
		VALUES
		(
			#{appId}
		,	#{appNm}
		,	#{corpId}
		,	#{projectId}

		,	'ON'
		,	#{fcmPackageName}
		,	#{fcmServerKey}
		,	#{senderId}
		,	#{apnsCetificationByteArray}
		,	#{apnsFileName}
		,	#{iosAppId}
		,	#{teamKey}
		,	#{keyId}
		,	#{userId}
		,	NOW()
		)

mapper.update:
/* channel.updatePushManage */
	UPDATE	cm_push.T_PUSH_CONFIG
	SET		APP_NM				= #{appNm}
	,		FCM_PACKAGE_NAME	= #{fcmPackageName}
	,		FCM_SERVER_KEY		= #{fcmServerKey}
	,		SENDER_ID			= #{senderId}
	
	,		APNS_CETIFICATION	= #{apnsCetificationByteArray}
	,		APNS_FILE_NAME		= #{apnsFileName}
	
	,		IOS_APP_ID			= #{iosAppId}
	,		TEAM_KEY			= #{teamKey}
	,		MOD_ID				= #{userId}
	,		MOD_DT				= NOW()
	WHERE	APP_ID				= #{appId}
	AND		PROJECT_ID			= #{projectId}
	AND		CORP_ID				= #{corpId}

mapper.delete:
/* channel.deletePushManage */
	DELETE	FROM cm_push.T_PUSH_CONFIG
	WHERE	APP_ID		= #{appId}
	AND		CORP_ID		= #{corpId}

mapper.select:
/* channel.selectMoCallbackListCnt */
	SELECT	COUNT(1) AS CNT
	FROM	cm.CM_MO_CALLBACK A
	WHERE	(PROJECT_ID = #{projectId} OR PROJECT_ID = 'ALL' )
	AND		A.CORP_ID = #{corpId}

mapper.insert:
/* channel.insertMoCallback */
INSERT	cm.CM_MO_CALLBACK
(
	MO_NUMBER
,	API_KEY
,	CORP_ID
,	MO_TYPE
,	PROJECT_ID
,	USE_YN
,	REG_DT
)
VALUES
(
	#{moNumber}
,	#{apiKey}
,	#{corpId}
,	#{moType}
,	#{projectId}
,	'Y'
,	NOW()
)

mapper.update:
/* channel.updateMoCallback */
	UPDATE	cm.CM_MO_CALLBACK
	SET		MO_TYPE		= #{moType}
	,		PROJECT_ID	= #{projectId}
	WHERE	MO_NUMBER	= #{moNumber}
	AND		API_KEY		= #{apiKey}
