mapper.select:
/* project.selectProjectList */ SELECT A.PROJECT_ID , A.CORP_ID , A.PROJECT_NAME , A.PROJECT_DESC , A.PAY_TYPE , CASE WHEN A.PAY_TYPE = 'Y' THEN '선불' WHEN A.PAY_TYPE = 'N' THEN '후불' ELSE '' END AS PAY_TYPE_NAME , A.BILL_ID , A.SMART_CH_CODE , A.SUBBILL_YN , A.SUBBILL_START_DAY , date_format(A.REG_DT, '%Y-%m-%d') AS REG_DT , A.UPD_DT , A.USE_YN , A.USE_CH_GRP_INFO , A.RESEND_TITLE , A.NOPAY_YN , A.SUBBILL_YN , date_format(A.SUBBILL_START_DAY, '%Y-%m-%d') as SUBBILL_START_DAY , ( SELECT COUNT(USER_ID) + 1 FROM cm_console.CM_PROJECT_USER WHERE PROJECT_ID = A.PROJECT_ID ) AS PROJECT_MEMBER_CNT FROM cm_console.CM_PROJECT A INNER JOIN cm_console.CM_PROJECT_USER B ON A.PROJECT_ID = B.PROJECT_ID WHERE 1=1 AND A.DEL_YN != 'Y'
AND B.USER_ID = #{loginId}
AND A.PROJECT_NAME LIKE CONCAT('%', #{srcProjectName}, '%')
AND A.USE_YN = #{srcUseYn}
ORDER BY A.PROJECT_ID DESC

/* project.checkProjectNameDuplicate */
SELECT	COUNT(PROJECT_ID) AS CNT
FROM	cm_console.CM_PROJECT
WHERE	PROJECT_NAME = #{checkProjectName}

/* project.selectProjectMaxId */
SELECT	MAX(PROJECT_ID) + 1 AS project_max_id
FROM	cm_console.CM_PROJECT

mapper.insert:
/* project.insertProject */
INSERT cm_console.CM_PROJECT
(
	PROJECT_ID
,	CORP_ID
,	PROJECT_NAME
,	PROJECT_DESC
,	PAY_TYPE
,	BILL_ID
,	SMART_CH_CODE
,	USE_CH_GRP_INFO
,	RESEND_TITLE
,	SUBBILL_YN
,	SUBBILL_START_DAY
,	NOPAY_YN
,	REG_DT
,	USE_YN
,	DEL_YN
)
VALUES
(
	#{projectId}
,	#{corpId}
,	#{projectName}
,	#{projectDesc}
,	#{payType}
,	'BILL_ID'
,	''
,	#{jsonInfo}
,	#{resendTitle}
,	#{subbillYn}
,	REPLACE(#{subbillStartDay}, '-', '')
,	'N'
,	NOW()
,	#{useYn}
,	'N'
)

mapper.update:
/* project.updateProject */
UPDATE	cm_console.CM_PROJECT
SET		PROJECT_NAME		= #{projectName}
,		PROJECT_DESC		= #{projectDesc}
,		USE_CH_GRP_INFO		= #{jsonInfo}
,		RESEND_TITLE		= #{resendTitle}
,		SUBBILL_YN			= #{subbillYn}
,		SUBBILL_START_DAY	= REPLACE(#{subbillStartDay}, '-', '')
,		USE_YN				= #{useYn}
WHERE	PROJECT_ID			= #{projectId}

/* project.updateProjectBillId */
UPDATE	cm_console.CM_PROJECT
SET		BILL_ID = #{billId}
WHERE	PROJECT_ID = #{projectId}

mapper.insert:
/* project.insertProjectUser */
INSERT	cm_console.CM_PROJECT_USER
(
	PROJECT_ID
,	CORP_ID
,	USER_ID
,	REG_ID
,	REG_DT
)
VALUES
(
	#{projectId}
,	#{corpId}
,	#{userId}
,	#{userId}
,	NOW()
)

mapper.select:
/* project.selectApikeyMaxId */
SELECT	IFNULL(MAX(API_KEY),0) + 1 AS api_max_id
FROM	cm.CM_APIKEY

/* project.selectProjectUseCnt */
SELECT
(
	SELECT	COUNT(MSG_KEY) AS CNT
	FROM	cm.CM_MSG
	WHERE	PROJECT_ID = #{projectId}
) + (
	SELECT	COUNT(WEB_REQ_ID) AS CNT
	FROM	cm_console.CM_WEB_MSG
	WHERE	PROJECT_ID = #{projectId}
) AS CNT

mapper.delete:
/* project.deleteProject */
UPDATE	cm_console.CM_PROJECT
SET		DEL_YN = 'Y'
WHERE	PROJECT_ID = #{projectId}

/* project.deleteProjectUser */
DELETE FROM cm_console.CM_PROJECT_USER
WHERE	PROJECT_ID = #{projectId}

/* project.deleteProjectApiKey */
DELETE FROM cm.CM_APIKEY
WHERE	PROJECT_ID = #{projectId}

mapper.select:
/* project.checkPreRegYn */
SELECT	CASE WHEN COUNT(CORP_ID) > 0 THEN 'Y' ELSE 'N' END AS PRE_REG_YN
FROM	cm_console.CM_SPECIAL_BUSI CSB 
WHERE	CORP_ID  = #{corpId}

mapper.insert:
