VALUES
(
	#{corpId}
,	#{reqType}
,	'REQ'
,	#{attachFileList}
,	#{loginId}
,	now()
)

mapper.sql:
SELECT SUB_CB.* , CRB.BRAND_NAME , CRB.CORP_ID FROM ( SELECT A.* FROM ( SELECT CHATBOT.* , case @vData when CHATBOT_ID then @vRank := @vRank + 1 else @vRank := 1 end AS RANKING , (@vData:=CHATBOT_ID) AS vData FROM ( select crcr.CHATBOT_ID , crcr.BRAND_ID , REPLACE(JSON_EXTRACT(crcr.CHATBOT_INFO , '$.subTitle[0]'), "\"", "") AS SUB_TITLE , CASE WHEN REPLACE(JSON_EXTRACT(crcr.CHATBOT_INFO , '$.rcsReply[0]'), "\"", "") = 1 THEN '예' ELSE '아니오' END AS RCS_REPLY_TEXT , REPLACE(JSON_EXTRACT(crcr.CHATBOT_INFO , '$.rcsReply[0]'), "\"", "") AS RCS_REPLY , crcr.APPROVAL_STATUS , date_format(crcr.REG_DT, '%Y-%m-%d') AS REG_DT , '' AS APPROVAL_DT from cm.CM_RCS_CHATBOT_REQ crcr where crcr.CORP_ID = #{corpId} and crcr.CHATBOT_ID not in ( select crc.CHATBOT_ID from cm.CM_RCS_CHATBOT crc where crc.CORP_ID = #{corpId} and REPLACE(JSON_EXTRACT(crc.CHATBOT_INFO , '$.isMainNum[0]'), "\"", "") = 'true' )
UNION
select crc.CHATBOT_ID , crc.BRAND_ID , REPLACE(JSON_EXTRACT(crc.CHATBOT_INFO , '$.subTitle[0]'), "\"", "") AS SUB_TITLE , CASE WHEN REPLACE(JSON_EXTRACT(crc.CHATBOT_INFO , '$.rcsReply[0]'), "\"", "") = 1 THEN '예' ELSE '아니오' END AS RCS_REPLY_TEXT , REPLACE(JSON_EXTRACT(crc.CHATBOT_INFO , '$.rcsReply[0]'), "\"", "") AS RCS_REPLY , crc.APPROVAL_STATUS , date_format(crc.REG_DT, '%Y-%m-%d') AS REG_DT , date_format(crc.APPROVAL_DT, '%Y-%m-%d') AS APPROVAL_DT from cm.CM_RCS_CHATBOT crc where crc.CORP_ID = #{corpId} ) CHATBOT, (SELECT @vRank:=0, @vData:='') as r ORDER BY CHATBOT.CHATBOT_ID, CHATBOT.REG_DT DESC ) A WHERE RANKING = 1 ) SUB_CB INNER JOIN cm.CM_RCS_BRAND CRB ON SUB_CB.BRAND_ID = CRB.BRAND_ID WHERE 1 = 1 AND (CRB.PROJECT_ID = #{projectId} OR CRB.PROJECT_ID = 'ALL')
AND SUB_CB.BRAND_NAME LIKE CONCAT('%',#{srcBrandName},'%')
AND SUB_CB.SUB_TITLE LIKE CONCAT('%',#{srcSubTitle},'%')
AND SUB_CB.CHATBOT_ID LIKE CONCAT('%',#{srcChatbotId},'%')
AND SUB_CB.RCS_REPLY = #{srcRcsReply}
AND SUB_CB.BRAND_NAME = #{srcBrandName}
AND SUB_CB.APPROVAL_STATUS = #{srcApprovalStatus}
AND CRB.BRAND_ID = #{brandId}

