mapper.select:
/* sendMessage.selectAppIdList APP ID 리스트 조회 */
    SELECT APLN_ID
    FROM CM_PUSH_SET
    WHERE 1=1
        AND CORP_ID = #{corpId}
        AND PROJECT_ID IN ('ALL', #{projectId})

/* sendMessage.selectCmCuList 주소록 카테고리 구성원 목록 조회 */ SELECT CCI.CU_INFO_ID , CCI.CU_NAME , CCI.CUID , CCI.HP_NUMBER , CCI.EMAIL FROM CM_ADDR_CATE_MEM CACM INNER JOIN CM_CU_INFO CCI ON CACM.CU_INFO_ID = CCI.CU_INFO_ID AND CCI.USE_YN = 'Y' WHERE 1=1
ORDER BY CU_INFO_ID
LIMIT ${offset}, #{listSize}

/* sendMessage.selectAllAppUserList 앱사용자 전체 조회 */ SELECT TPUI.CUID
, TPUI.PHONENO AS PHONE
, @ROWNUM := @ROWNUM + 1 AS CLI_KEY FROM cm_push.T_PUSH_USER_INFO TPUI INNER JOIN (SELECT @ROWNUM:=0) TMP WHERE TPUI.APPID = #{appId}

/* sendMessage.selectPayType 결제방식 조회 */
    SELECT CP.PAY_TYPE
    FROM CM_CORP CC
    INNER JOIN CM_PROJECT CP
        ON CC.CORP_ID = CP.CORP_ID
        AND CP.USE_YN = 'Y'
        AND CP.DEL_YN = 'N'
        AND CP.PROJECT_ID = #{projectId}
    WHERE CC.STATUS = 'USE'
        AND CC.CORP_ID = #{corpId}

/* sendMessage.selectMsgFeePerOne 메시지 발송 가격 */
    SELECT MAX(IFNULL(CCPU.PREE_FEE, CPU.PREE_FEE)) AS MAX_FEE
    FROM cm_bo.CM_PRODUCT_UNIT CPU
    LEFT OUTER JOIN CM_CORP_PRODUCT_UNIT CCPU
        ON CPU.PRODUCT_CODE = CCPU.PRODUCT_CODE
        AND CCPU.CORP_ID = #{corpId}
        AND CCPU.USE_YN = 'Y'
    WHERE CPU.USE_YN = 'Y'
        AND CPU.PRODUCT_CODE IN #{item}

mapper.insert:
/* sendMessage.insertCmWebMsg - 웹 발송 내역 등록 */
    INSERT INTO CM_WEB_MSG
    (
        WEB_REQ_ID
        , CORP_ID
        , PROJECT_ID
        , API_KEY
        , CH_STRING
        , MSG_INFO
        , SENDER_CNT
        , CALLBACK
        , CAMPAIGN_ID
        , SENDER_TYPE
        , STATUS
        , RESV_SENDER_YN
        , REG_DT
    
        ,REQ_DT
    
    )
    VALUES
    (
        #{webReqId}
        , #{corpId}
        , #{projectId}
        , #{apiKey}
        , #{chString}
        , #{msgInfo}
        , #{senderCnt}
        , #{callback}
        , #{campaignId}
        , #{senderType}
        , #{status}
        , #{resvSenderYn}
        , NOW()
    
        , DATE_FORMAT(#{reqDt}, '%Y-%m-%d %H:%i')
    
       )

/* template.insertCmMsg - 채널별 메시지 내역 등록 */
    INSERT
        INTO cm.CM_MSG
    (
        YMD
        , MSG_KEY
        , CORP_ID
        , PROJECT_ID
        , API_KEY
        , PAY_TYPE
        , CLI_KEY
        , SENDER_TYPE
        , REQ_CH
        , PRODUCT_CODE
        , STATUS
        , REG_DT
        , FINAL_CH
        , PHONE
        , PUSH_APP_ID
        , PUSH_CUID
        , CALLBACK
        , WEB_REQ_ID
        , GW_RESULT_CODE
        , GW_RESULT_DESC
        , FB_REQ_CNT
    ) VALUES (
        DATE_FORMAT(NOW(), '%Y-%m-%d')
        , #{msgKey}
        , #{corpId}
        , #{projectId}
        , #{apiKey}
        , 'POST'
        , #{cliKey}
        , #{senderType}
        , #{reqCh}
        , #{productCode}
        , '9999'
        , CONCAT(DATE_FORMAT(NOW(3), '%Y-%m-%dT%H:%i:%s'), LEFT(DATE_FORMAT(NOW(3), '.%f'), 4), '+09')
        , #{finalCh}
        , #{phone}
        , #{pushAppId}
        , #{pushCuid}
        , #{callback}
        , #{webReqId}
        , #{gwResultCode}
        , #{gwResultDesc}
        , '0'
    )
