mapper.select:
/* sendMessage.selectAppIdList APP ID 리스트 조회 */
    SELECT APLN_ID
    FROM CM_PUSH_SET
    WHERE 1=1
        AND CORP_ID = #{corpId}
        AND PROJECT_ID IN ('ALL', #{projectId})

/* sendMessage.selectCallbackList 발신번호 조회 */
    SELECT CHATBOT_ID AS CALLBACK /* 챗봇(대화방)ID, A2P의 경우 발신번호(mdn)과 동일 */
    FROM cm.CM_RCS_CHATBOT
    WHERE SVC_TYPE = 'A2P'
        AND CORP_ID = #{corpId}

/* sendMessage.selectCmCuList 주소록 카테고리 구성원 목록 조회 */ SELECT CCI.CU_INFO_ID , CCI.CU_NAME , CCI.CUID , CCI.HP_NUMBER , CCI.EMAIL FROM CM_ADDR_CATE_MEM CACM INNER JOIN CM_CU_INFO CCI ON CACM.CU_INFO_ID = CCI.CU_INFO_ID AND CCI.USE_YN = 'Y' WHERE 1=1
ORDER BY CU_INFO_ID
LIMIT ${offset}, #{listSize}

/* sendMessage.selectPayType 결제방식 조회 */
    SELECT CP.PAY_TYPE
    FROM CM_CORP CC
    INNER JOIN CM_PROJECT CP
        ON CC.CORP_ID = CP.CORP_ID
        AND CP.USE_YN = 'Y'
        AND CP.DEL_YN = 'N'
        AND CP.PROJECT_ID = #{projectId}
    WHERE CC.STATUS = 'USE'
        AND CC.CORP_ID = #{corpId}

mapper.insert:
/* sendMessage.insertCmWebMsg - 웹 발송 내역 등록 */
    INSERT INTO CM_WEB_MSG
    (
        WEB_REQ_ID
        , CORP_ID
        , PROJECT_ID
        , API_KEY
        , CH_STRING
        , MSG_INFO
        , SENDER_CNT
        , CALLBACK
        , CAMPAIGN_ID
        , SENDER_TYPE
        , STATUS
        , RESV_SENDER_YN
        , REG_DT
    
        ,REQ_DT
    
    )
    VALUES
    (
        #{webReqId}
        , #{corpId}
        , #{projectId}
        , #{apiKey}
        , #{chString}
        , #{msgInfo}
        , #{senderCnt}
        , #{callback}
        , #{campaignId}
        , #{senderType}
        , #{status}
        , #{resvSenderYn}
        , NOW()
    
        , DATE_FORMAT(#{reqDt}, '%Y-%m-%d %H:%i')
    
       )
