mapper.select:
/* statisticsAdmin.selectStatisList 일별/월별 전송건수 조회 */ SELECT
DATE_FORMAT(YMD, '%Y.%m.%d') AS SEND_DATE
DATE_FORMAT(YMD, '%Y.%m') AS SEND_DATE
, PROJECT_ID , ( SELECT PROJECT_NAME FROM cm_console.CM_PROJECT WHERE DEL_YN = 'N' AND CORP_ID = #{corpId} AND PROJECT_ID = A.PROJECT_ID) AS PROJECT_NAME , API_KEY , SUM(TOT_CNT) AS TOT_CNT , SUM(SUCC_CNT) AS SUCC_CNT , SUM(FAIL_CNT) AS FAIL_CNT , IFNULL(CONCAT(ROUND(SUM(SUCC_CNT)/SUM(TOT_CNT) * 100), '%'), '0%') AS SUCC_RATIO FROM cm.CM_STAT_CH_DAY A WHERE CORP_ID = #{corpId}
AND PROJECT_ID = #{searchProjectId}
AND YMD BETWEEN #{searchStartDate} AND #{searchEndDate}
AND YMD BETWEEN CONCAT(#{searchStartMonth},'-01') AND CONCAT(#{searchEndMonth}, '-31')
AND CH = #{searchKakaoType}
AND CH IN ('ALIMTALK', 'FRIENDTALK')
AND CH = 'SMS'
AND CH = 'MMS' AND PRODUCT_CODE = 'LMS'
AND CH = 'MMS' AND PRODUCT_CODE = 'MMS'
AND CH IN ('SMS', 'MMS')
AND CH = 'MO'
AND PRODUCT_CODE = #{searchSmsmmsType}
AND CH = #{searchChanType}
GROUP BY SEND_DATE, PROJECT_ID, API_KEY
