mapper.select:
/* template.selectSmsTmpltList SMS 템플릿 리스트 조회 */ SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM , CST.TMPLT_ID , CST.TMPLT_NAME , CST.TMPLT_DESC , CST.SENDER_TYPE , CST.PROJECT_ID , IF(CST.PROJECT_ID = 'ALL', '공용', '전용') AS PROJECT_ID_NM , CST.MSG_KIND , cm_console.fnc_get_cd_name('MSG_KIND', CST.MSG_KIND, 1) AS MSG_KIND_NAME , CST.TMPLT_TITLE , CST.TMPLT_CONTENT , CST.RCVBLC_NUMBER , CST.IMG_INFO_LIST , DATE_FORMAT(CST.REG_DT, '%Y-%m-%d %H:%i:%S') AS REG_DT , CST.REG_ID , cm_console.fnc_get_user_name(CST.REG_ID) AS REG_NM , CST.UPD_DT , CST.UPD_ID FROM CM_SMS_TMPLT CST, (SELECT @ROWNUM:=0) TMP WHERE 1=1
ORDER BY REG_DT DESC
LIMIT ${offset}, #{listSize}

mapper.update:
/* template.updateSmsTmplt - SMS 템플릿 수정 */
    UPDATE CM_SMS_TMPLT
    SET
        TMPLT_NAME = #{tmpltName}
