mapper.sql:
AND CORP_ID = #{corpId}

    
    AND TMPLT_ID LIKE CONCAT('%', #{searchText}, '%')
    
    
    AND TMPLT_NAME LIKE CONCAT('%', #{searchText}, '%')
    


    
    AND REG_DT BETWEEN CONCAT(#{searchStartDate}, ' 00:00:00') AND CONCAT(#{searchEndDate}, ' 23:59:59')
    


    
        AND PROJECT_ID IN
        
            'ALL'
            #{projectId}
        
    
    
        AND PROJECT_ID IN ('ALL', #{projectId})
    


    AND TMPLT_ID = #{tmpltId}

mapper.select:
/* template.selectPushTemplate 푸시 템플릿 리스트 조회 */ SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM , CIF.TMPLT_ID , CIF.TMPLT_NAME , CIF.MSG_TYPE , cm_console.fnc_get_cd_name('MSG_TYPE', CIF.MSG_TYPE, 1) AS MSG_TYPE_NAME , CIF.MSG_KIND , cm_console.fnc_get_cd_name('MSG_KIND', CIF.MSG_KIND, 1) AS MSG_KIND_NAME , CIF.PROJECT_ID , IF(CIF.PROJECT_ID = 'ALL', '공용', '전용') AS PROJECT_ID_NM , CIF.TMPLT_TITLE , CIF.TMPLT_CONTENT , CIF.RCVBLC_NUMBER , CIF.IMG_URL , CIF.FILE_ID , DATE_FORMAT(CIF.REG_DT, '%Y-%m-%d %H:%i:%S') AS REG_DT , CIF.REG_ID , cm_console.fnc_get_user_name(CIF.REG_ID) AS REG_NM , CIF.UPD_DT , CIF.UPD_ID , CIF.ADTN_INFO FROM CM_PUSH_TMPLT CIF, (SELECT @ROWNUM:=0) TMP WHERE 1=1
ORDER BY REG_DT DESC
LIMIT ${offset}, #{listSize}

mapper.delete:
/* template.deletePushTemplate - 푸시 템플릿 삭제 */
    DELETE
    FROM CM_PUSH_TMPLT
    WHERE CORP_ID = #{corpId}
        AND TMPLT_ID IN #{item}

mapper.insert:
/* template.insertPushTemplate - 푸시 템플릿 등록 */
    INSERT
        INTO CM_PUSH_TMPLT
    (
        TMPLT_ID
        , TMPLT_NAME
        , MSG_TYPE
        , MSG_KIND
        , TMPLT_TITLE
        , TMPLT_CONTENT
        , RCVBLC_NUMBER
    
        , IMG_URL
        , FILE_ID
    
        , REG_DT
        , REG_ID
        , UPD_DT
        , UPD_ID
        , ADTN_INFO
        , PROJECT_ID
        , CORP_ID
    ) VALUES (
        #{tmpltId}
        , #{tmpltName}
        , #{msgType}
        , #{msgKind}
        , #{tmpltTitle}
        , #{tmpltContent}
        , #{rcvblcNumber}
    
        , #{imgUrl}
        , #{fileId}
    
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
        , #{adtnInfo}
        , #{projectId}
        , #{corpId}
    )

mapper.update:
/* template.insertPushTemplate - 푸시 템플릿 수정 */
    UPDATE CM_PUSH_TMPLT
    SET
        TMPLT_NAME = #{tmpltName}
        , MSG_TYPE = #{msgType}
        , MSG_KIND = #{msgKind}
        , PROJECT_ID = #{projectId}
        , TMPLT_TITLE = #{tmpltTitle}
        , TMPLT_CONTENT = #{tmpltContent}
        , RCVBLC_NUMBER = #{rcvblcNumber}
    
        
            , IMG_URL = #{imgUrl}
            , FILE_ID = #{fileId}
        
        
            , IMG_URL = ''
            , FILE_ID = ''
        
    
        , UPD_DT = NOW()
        , UPD_ID = #{userId}
        , ADTN_INFO = #{adtnInfo}
    WHERE TMPLT_ID = #{tmpltId}

