        , #{tmpltName}
        , #{corpId}
        , #{msgKind}
        , #{tmpltContent}
    
        , #{fileId}
        , #{imgUrl}
        , #{imgLink}
        , #{wideImgYn}
    
        , #{tmpltButtonsStr}
        , NOW()
        , #{userId}
        , NOW()
        , #{userId}
        , #{projectId}
    )

