mapper.select:
/* use.selectUseHistoryListCnt */ SELECT COUNT(*) CNT FROM cm_console.CM_PROJECT A INNER JOIN (SELECT A.CORP_ID , A.PROJECT_ID , ROUND(SUM(IFNULL(A.AMOUNT, 0))) SUM_CH_GRP_AMOUNT , COUNT(B.CH_GRP) CNT FROM ( SELECT IFNULL(msg.AMOUNT, 0) AS AMOUNT , msg.CORP_ID , msg.PROJECT_ID , msg.FINAL_CH FROM cm.CM_MSG msg WHERE msg.YMD BETWEEN CONCAT(SUBSTRING(#{searchMonth}, 1, 4), '-', SUBSTRING(#{searchMonth}, 5, 6), '-01') AND LAST_DAY(CONCAT(SUBSTRING(#{searchMonth}, 1, 4), '-', SUBSTRING(#{searchMonth}, 5, 6), '-01') ) AND msg.CORP_ID = #{corpId} AND msg.GW_RESULT_CODE = '10000' AND msg.CLI_RESULT_CODE = '10000' ) A INNER JOIN cm_console.CM_CH_INFO B ON A.FINAL_CH = B.CH_NAME WHERE A.CORP_ID = #{corpId}
AND A.PROJECT_ID IN ( SELECT A.PROJECT_ID FROM cm_console.CM_PROJECT_USER A INNER JOIN cm_console.CM_USER B WHERE A.USER_ID = B.USER_ID AND B.DEL_YN = 'N' AND B.USER_ID = #{userId} )
GROUP BY A.CORP_ID, A.PROJECT_ID) B USING(PROJECT_ID, CORP_ID) WHERE A.USE_YN = 'Y' AND A.DEL_YN = 'N' AND A.CORP_ID = #{corpId}

/* use.selectUseHistoryList */ SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM , A.CORP_ID , A.PROJECT_ID , A.PROJECT_NAME , A.NOPAY_YN , CASE WHEN A.NOPAY_YN = 'Y' THEN '무과금' ELSE CASE WHEN A.PAY_TYPE = 'Y' THEN '선불' WHEN A.PAY_TYPE = 'N' THEN '후불' ELSE '' END END AS PAY_TYPE_NAME , CASE WHEN A.NOPAY_YN = 'Y' THEN 'F' ELSE A.PAY_TYPE END AS PAY_TYPE , DATE_FORMAT(A.REG_DT, '%Y-%m-%d') AS REG_DT , A.USE_CH_GRP_INFO , B.CNT , B.SUM_CH_GRP_AMOUNT FROM cm_console.CM_PROJECT A INNER JOIN (SELECT @rownum:=0) T2 INNER JOIN (SELECT A.CORP_ID , A.PROJECT_ID , ROUND(SUM(IFNULL(A.AMOUNT, 0))) SUM_CH_GRP_AMOUNT , COUNT(B.CH_GRP) CNT FROM ( SELECT IFNULL(msg.AMOUNT, 0) AS AMOUNT , msg.CORP_ID , msg.PROJECT_ID , msg.FINAL_CH FROM cm.CM_MSG msg WHERE msg.YMD BETWEEN CONCAT(SUBSTRING(#{searchMonth}, 1, 4), '-', SUBSTRING(#{searchMonth}, 5, 6), '-01') AND LAST_DAY(CONCAT(SUBSTRING(#{searchMonth}, 1, 4), '-', SUBSTRING(#{searchMonth}, 5, 6), '-01') ) AND msg.CORP_ID = #{corpId} AND msg.GW_RESULT_CODE = '10000' AND msg.CLI_RESULT_CODE = '10000' ) A INNER JOIN cm_console.CM_CH_INFO B ON A.FINAL_CH = B.CH_NAME WHERE A.CORP_ID = #{corpId}
AND A.PROJECT_ID IN ( SELECT A.PROJECT_ID FROM cm_console.CM_PROJECT_USER A INNER JOIN cm_console.CM_USER B WHERE A.USER_ID = B.USER_ID AND B.DEL_YN = 'N' AND B.USER_ID = #{userId} )
GROUP BY A.CORP_ID, A.PROJECT_ID) B USING(PROJECT_ID, CORP_ID) WHERE A.USE_YN = 'Y' AND A.DEL_YN = 'N' AND A.CORP_ID = #{corpId}
LIMIT #{offset}, #{listSize}

/* use.selectIsExistsCorpProductUnit */
SELECT EXISTS (
    SELECT  1
    FROM    cm_console.CM_CORP_PRODUCT_UNIT
    WHERE CORP_ID = #{corpId}
    LIMIT 1
)   AS ISEXISTS

/* use.selectSixMonthUseCnt 6개월간 이용건수 조회 */ SELECT DATE_FORMAT(DATE_ADD(CONCAT(#{searchStartDate},'-01'), INTERVAL A.a+(4*B.a) MONTH), '%Y-%m') AS YM , (SELECT SUM(SUCC_CNT) FROM cm.CM_STAT_CH_DAY WHERE YMD BETWEEN DATE_FORMAT(DATE_ADD(CONCAT(#{searchStartDate},'-01'), INTERVAL A.a+(4*B.a) MONTH), '%Y-%m-01') AND DATE_FORMAT(DATE_ADD(CONCAT(#{searchStartDate},'-01'), INTERVAL A.a+(4*B.a) MONTH), '%Y-%m-31') AND CORP_ID = #{corpId}
AND PRODUCT_CODE = ''
'SMS','LMS','MMS'
'RTPL','RSMS','RLMS','RMMS'
'KALT1','KFRT1','KFRM2','KFRM3'
'PUSH'
AND PROJECT_ID = ''
#{item.projectId}
) AS CNT FROM (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3) AS A , (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2) AS B WHERE DATE_ADD(CONCAT(#{searchStartDate},'-01'), INTERVAL A.a+(4*B.a) MONTH) BETWEEN CONCAT(#{searchStartDate},'-01') AND CONCAT(#{searchEndDate},'-31') ORDER BY 1
